Numpad = list[list[str]]


def get_code(numpad: Numpad, ins: str) -> str:
    start = [0, 0]
    for y, row in enumerate(numpad):
        for x, ch in enumerate(row):
            if ch == '5':
                start = [x, y]
                break

    code = ""
    for line in ins.strip().splitlines():
        for ch in line:
            dx = (ch == 'R') - (ch == 'L')
            dy = (ch == 'D') - (ch == 'U')

            x, y = start
            x = max(0, x + dx)
            y = max(0, y + dy)

            try:
                if numpad[y][x] != "":
                    start = [x, y]
            except IndexError:
                pass
        x, y = start
        code += str(numpad[y][x])
    return code


numpad = [['1', '2', '3'],
          ['4', '5', '6'],
          ['7', '8', '9']]

ins = """ULL
RRDDD
LURDL
UUUUD
"""

print(f"example 1: {get_code(numpad, ins)}")

